package basic;

public class ForloopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//loop - the purpose is repeating a block of code
				//1. for loop
				//2. while loop
				
				for(int i=1; i<=100; i = i + 1) {
					System.out.println("Hello World");
				}//end of for
	
	
	}

}
